import logo from "../images/logo-bookmark.svg";
import logoWhite from "../images/logo-bookmark-white-menu.svg";
import hamburger from "../images/icon-hamburger.svg";
import close from "../images/icon-close.svg";
import { useState } from "react";

export default function Header() {
  const [showMenu, setShowMenu] = useState(false);

  function toggleMenu() {
    setShowMenu(!showMenu);
  }

  return (
    <header className={`${showMenu ? "fixed" : ""} px-8 py-10 w-full ${showMenu ? "bg-[#252b46]" : ""}`}>
      <div className={`flex justify-between items-center ${showMenu ? "mb-5" : ""}`}>
        <img className="z-20" src={showMenu ? logoWhite : logo} alt="Bookmark logo" />
        <button className="z-20 hover:cursor-pointer" onClick={toggleMenu}>
          <img src={showMenu ? close : hamburger} alt="Menu Icon" />
        </button>
      </div>
      {showMenu && (
        <ul className="z-10 absolute top-16 left-0 w-full h-[100vh] text-center bg-[#252b46]">
          <li className="border-t border-b border-white uppercase text-xl">Features</li>
          <li className="border-b border-white uppercase text-xl">Pricing</li>
          <li className="border-b border-white uppercase text-xl">Contact</li>
        </ul>
      )}
    </header>
  );
}
