import Button from "../buttons/Button";

import image from "../images/illustration-features-tab-1.svg";

export default function Bookmarking() {
  return (
    <div className="relative grid gap-20 my-[3.5625rem] lg:flex lg:my-11">
      <img
        className="m-auto"
        src={image}
        alt="Drawing of a internet site home screen"
      />
      <div className="absolute top-[2.1875rem] left-[-2rem] h-[12.75rem] w-[97%] bg-[#5368df] -z-10 rounded-r-full m-auto lg:left-[-15rem] lg:w-[50%] lg:top-20 lg:h"></div>
      <div className="grid gap-3.5">
        <h3 className="text-2xl font-medium tracking-wide">
          Bookmark in one click
        </h3>
        <p className="text-[#9194a1]">
          Organize your bookmarks however you like. Our simple drag-and-drop
          interface gives you complete control over how you manage your
          favourite sites.
        </p>
        <Button bg="bg-[#5368df]" textColor="text-white" text="More Info" />
      </div>
    </div>
  );
}
