import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";

import Bookmarking from "./Bookmarking";
import Searching from "./Searching";
import Sharing from "./Sharing";
import { useState } from "react";

export default function Features() {
    const [displayDecoration, setDisplayDecoration] = useState('hidden')

    function selectFeature(e, index) {
        const decorations = document.querySelectorAll(".decoration")

    }

  return (
    <div className="grid gap-3 px-8 my-[8.5rem]">
      <h2 className="text-2xl font-medium tracking-wide">Features</h2>

      <p className="text-[#9194a1]">
        Our aim is to make it quick and easy for you to access your favourite
        websites. Your bookmarks sync between your devices so you can access
        them on the go.
      </p>

      <Router>
        <ul className="border-t border-b mt-7 tracking-wider">
          <li onClick={selectFeature} className="relative border-b pb-[0.875rem] pt-4">
            <Link to="/">Simple Bookmarking</Link>
            <div className={`${() => displayDecoration(1)} decoration translate-y-3.5 m-auto w-[8.75rem] h-1 bg-[#fa5757]`}></div>
          </li>
          <li onClick={selectFeature} className="relative border-b pb-[0.875rem] pt-4">
            <Link to="/searching">Speedy Searching</Link>
            <div className={`${() => displayDecoration()} decoration translate-y-3.5 m-auto w-[8.75rem] h-1 bg-[#fa5757]`}></div>
          </li>
          <li onClick={selectFeature} className="relative pb-[0.875rem] pt-4">
            <Link to="/sharing">Easy Sharing</Link>
            <div className={`${() => displayDecoration()} decoration translate-y-3.5 m-auto w-[8.75rem] h-1 bg-[#fa5757]`}></div>
          </li>
        </ul>
        <Routes>
          <Route path="/" element={<Bookmarking />} />
          <Route path="/searching" element={<Searching />} />
          <Route path="/sharing" element={<Sharing />} />
        </Routes>
      </Router>
    </div>
  );
}
